INSERT INTO `mail_template` (`ID`, `Body`, `VerifiedBuild`) VALUES ('426', '$n!\r\n\r\nЯ заметил, что тебе удалось доставить потерянную посылку. Приношу свою благодарность!\r\n\r\nУ нас не хватает рук, и услуги хорошего курьера нам не помешают.\r\n\r\nЕсли тебя заинтересовало мое предложение, жду тебя в офисе за Торговой палатой волшебников.\r\n\r\n– Почтальон', '26124'); 
INSERT INTO `mail_template_locale` (`ID`, `locale`, `Body_lang`, `VerifiedBuild`) VALUES ('426', 'enUS', '$n!\r\n\r\nI noticed that you managed to deliver the lost parcel. I offer my thanks!\r\n\r\nWe do not have enough hands, and the services of a good courier will not prevent us.\r\n\r\nIf you are interested in my proposal, I\'m waiting for you at the office behind the Chamber of Wizards.\r\n\r\n- Postman', '26124'); 
